def swap_columns(image_name):
    image = Image.open(image_name)
    data = asarray(image).copy()

    print(len(data[0]))
    a_list = list(range(0, len(data[0])))

    print(a_list)
    b_list = a_list.copy()

    random.shuffle(b_list)
    print(b_list)

    print(data[0][0])
    print(data[0][1])


    #data=data[::-1]
    data[:, a_list] = data[:, b_list]
    print(data[0][0])
    print(data[0][1])

    return data


